targets.rawSql:
SELECT $__timeGroup(ts, $__interval) AS "time", 
       level AS metric, count(*) AS value 
FROM ${log}
WHERE $__timeFilter(ts)
GROUP BY 1, 2
ORDER BY 1, 2;

SELECT sid, count(*) AS count, count(*) FILTER ( WHERE code != '00000' ) AS error
FROM ${log}
WHERE $__timeFilter(ts)
GROUP BY sid ORDER BY 1;

SELECT $__timeGroup(ts, $__interval) AS "time", 
       level AS metric, count(*) AS value 
FROM ${log}
WHERE $__timeFilter(ts) AND sid = '$sid'
GROUP BY 1, 2
ORDER BY 1, 2;
