SELECT array_agg(u.usename)::TEXT
FROM monitor.pg_stat_statements s LEFT OUTER JOIN pg_user u ON s.userid = u.usesysid
WHERE dbid = (SELECT oid FROM pg_database WHERE datname = '${datname}') AND queryid = '$query';

SELECT s.*, u.usename
FROM monitor.pg_stat_statements s LEFT OUTER JOIN pg_user u ON s.userid = u.usesysid
WHERE dbid = (SELECT oid FROM pg_database WHERE datname = '${datname}') AND queryid = '$query';
